void setProjectionMatrix(int shaderProgram, mat4 projectionMatrix)
{
    glUseProgram(shaderProgram);
    GLuint projectionMatrixLocation = glGetUniformLocation(shaderProgram, "projectionMatrix");
    glUniformMatrix4fv(projectionMatrixLocation, 1, GL_FALSE, &projectionMatrix[0][0]);
}

void setViewMatrix(int shaderProgram, mat4 viewMatrix)
{
    glUseProgram(shaderProgram);
    GLuint viewMatrixLocation = glGetUniformLocation(shaderProgram, "viewMatrix");
    glUniformMatrix4fv(viewMatrixLocation, 1, GL_FALSE, &viewMatrix[0][0]);
}

void setWorldMatrix(int shaderProgram, mat4 worldMatrix)
{
    glUseProgram(shaderProgram);
    GLuint worldMatrixLocation = glGetUniformLocation(shaderProgram, "worldMatrix");
    glUniformMatrix4fv(worldMatrixLocation, 1, GL_FALSE, &worldMatrix[0][0]);
}



static float barryCentric(vec3 p1, vec3 p2, vec3 p3, vec3 pos) {
    float det = (p2.z - p3.z) * (p1.x - p3.x) + (p3.x - p2.x) * (p1.z - p3.z);
    float l1 = ((p2.z - p3.z) * (pos.x - p3.x) + (p3.x - p2.x) * (pos.y - p3.z)) / det;
    float l2 = ((p3.z - p1.z) * (pos.x - p3.x) + (p1.x - p3.x) * (pos.y - p3.z)) / det;
    float l3 = 1.0f - l1 - l2;
    return l1 * p1.y + l2 * p2.y + l3 * p3.y;
}


vector<vec3> heightGridMap[400][400];


float getHieghtofTerrain(float xPos, float zPos) {
    float height;
    //there is a total of 1600 squares i.e. 40x40
    //after scaling grid length X and Z = 40*30 =1200
    //each grid square is 30 units in length;
    int gridX = int(glm::floor(xPos / 30));
    int gridZ = int(glm::floor(zPos / 30));

    //if camera position is outside of terrain, return 0
    if (gridX > 40 || gridZ > 40 || gridX <= 0 || gridZ <= 0) {
        return 0;
    }
    //from square to triangle:
    //since we are drawing in triangle, we need to know which triangle we are standing on given a square
    float xCoord = (int(xPos) % 30) / 30;
    float zCoord = (int(zPos) % 30) / 30;

    //fetching 4 verts from grid:
    cout << "gx: " << gridX << " gz: " << gridZ << endl;
    float tr = heightGridMap[gridX][gridZ].at(0).y;
    float br = heightGridMap[gridX][gridZ].at(1).y;
    float bl = heightGridMap[gridX][gridZ].at(2).y;
    float tl = heightGridMap[gridX][gridZ].at(3).y;

    //now using barry centric interpolation to calculate height at mid point of triangle
    if (xCoord <= (1 - zCoord)) {

        //top left
        //top right
        //bottom left

        height = barryCentric(
            vec3(0, tl, 0),
            vec3(1, tr, 0),
            vec3(0, bl, 1),
            vec3(xCoord, zCoord, 0));
    }
    else {
        //top right
        //bottom right
        //bottom left
        height = barryCentric(
            vec3(1, tr, 0),
            vec3(1, br, 1),
            vec3(0, bl, 1),
            vec3(xCoord, zCoord, 0));
    }

    return height;
}

